/* Used to retrieve the platform specific
   version of electron that is downloaded at runtime */
const path = require('path')
const os = require('os');

switch (os.platform()) {
  case 'darwin':
    module.exports = path.join(__dirname, 'dist/Electron.app/Contents/MacOS/Electron');
    break;
  case 'freebsd':
  case 'linux':
    module.exports = path.join(__dirname, 'dist/electron');
    break;
  case 'win32':
  default:
    module.exports = path.join(__dirname, 'dist/electron.exe');
    break;
}